import React, { FC, FormEvent, useState } from 'react';

interface BookNowFormProps {
  isOpen: boolean;
  onClose?: () => void;
}

interface FormData {
  name: string;
  instagramHandle: string;
  email: string;
  phone: string;
  date: string;
  designType: 'flash' | 'custom';
  source: string;
  message: string;
  photos: FileList | null;
}

const BookNowForm: FC<BookNowFormProps> = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState<FormData>({
    name: '',
    instagramHandle: '',
    email: '',
    phone: '',
    date: '',
    designType: 'flash',
    source: 'Instagram',
    message: '',
    photos: null
  });

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log(formData);
    onClose?.();
  };

  const handleClose = () => {
    onClose?.();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white text-gray-800 p-6 rounded-md shadow-lg max-w-lg mx-auto">
        <h2 className="text-2xl text-[#c89d7c] mb-4">Book Your Appointment</h2>
        <form onSubmit={handleSubmit}>
          {/* Form fields */}
          <label className="block text-gray-600 mb-2">Name:</label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            className="w-full mb-4 p-2 border border-gray-300 rounded-md"
            required
          />
          {/* Add all other form fields similarly */}
          
          <div className="flex justify-end space-x-4">
            <button
              type="submit"
              className="bg-[#c89d7c] text-white px-4 py-2 rounded-md"
            >
              Submit
            </button>
            <button
              type="button"
              onClick={handleClose}
              className="bg-[#c89d7c] text-white px-4 py-2 rounded-md"
            >
              Close
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BookNowForm;
