import React, { FC, useEffect, useRef } from 'react';
import BookNowButton from './BookNowButton';
import BookNowForm from './BookNowForm';
import { gsap } from 'gsap';
import { TextPlugin } from 'gsap/TextPlugin';

gsap.registerPlugin(TextPlugin);

interface HeroProps {
  isOpen: boolean;
  handleOpenBookNow: () => void;
  handleCloseBookNow: () => void;
}

const Hero: FC<HeroProps> = ({ isOpen, handleOpenBookNow, handleCloseBookNow }) => {
  const textRef = useRef<HTMLSpanElement>(null);
  const cowRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const text = 'KowTattys';
    const textArray = text.split('');
    const textTimeline = gsap.timeline({ repeat: -1 });

    textArray.forEach((char, index) => {
      textTimeline.to(textRef.current, {
        duration: 2.1,
        text: textArray.slice(0, index + 1).join(''),
      });
    });

    textTimeline.eventCallback('onComplete', () => {
      const cowTimeline = gsap.timeline();
      cowTimeline.to(cowRef.current, {
        duration: 1,
        opacity: 1,
      });
      cowTimeline.to(cowRef.current, {
        duration: 2,
        repeat: -1,
        yoyo: true,
        y: -10,
      });
    });
  }, []);

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <span ref={textRef} className="text-5xl font-bold" />
      <div
        ref={cowRef}
        className="absolute bottom-0 left-1/2 transform -translate-x-1/2 opacity-1"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-24 w-24"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 10l-4 4m0 0l4 4m-4-4h3v-1a4 4 0 01 0-8H9a4 4 0 01 0 8v1h3z"
          />
        </svg>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6 absolute bottom-0 left-1/2 transform -translate-x-1/2"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
          />
        </svg>
      </div>
      <BookNowButton handleOpenBookNow={handleOpenBookNow} />
      <BookNowForm isOpen={isOpen} onClose={handleCloseBookNow} />
    </div>
  );
};

export default Hero;
